        public void FilterByDepartments(string selectedDepartment)
        {
            if (selectedDepartment == Department.TermForAllDeps)
            {
                filteredListByDeps = new List<User>(items);
            }
            else
            {
                filteredListByDeps.Clear();
                foreach (User user in items)
                {
                    if (user.Department == selectedDepartment)
                    {
                        filteredListByDeps.Add(user);
                    }
                }
            }
        }

        public List<User> OrderByFirstName()
        {
            sortedList = filteredListByDeps.OrderBy(o => o.FirstName).ToList();
            return sortedList;
        }

        public List<User> OrderByLastName()
        {
            sortedList = filteredListByDeps.OrderBy(o => o.LastName).ToList();
            return sortedList;
        }

        public List<User> OrderByDepartment()
        {
            sortedList = filteredListByDeps.OrderBy(o => o.Department).ToList();
            return sortedList;
        }

        public List<User> OrderByLandlineNbr()
        {
            sortedList = filteredListByDeps.OrderBy(o => o.LandlineNbr).ToList();
            return sortedList;
        }

        public List<User> OrderByMobileNbr()
        {
            sortedList = filteredListByDeps.OrderBy(o => o.MobileNbr).ToList();
            return sortedList;
        }

        public List<User> SearchUser(string input)
        {
            sortedList.Clear();

            foreach (User user in filteredListByDeps)
            {
                if (user.FirstName.ToLower().Contains(input) || user.LastName.ToLower().Contains(input))
                    sortedList.Add(user);
            }
            return sortedList;
        }
    }

